sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) {
        return;
    }
	// This is our main function that contains all the logic.
    try {
        var isHalfDay = g_form.getValue('is_half_day') === 'true';
        var startDate = g_form.getValue('start_date');
        var endDate = g_form.getValue('end_date');

        // Clear any previous info messages
        g_form.hideFieldMsg('end_date', true);

        // --- SCENARIO 1: "Is Half Day" is CHECKED ---
        if (isHalfDay) {
            g_form.setValue('duration', '0.50');

            // Enforce that end date must match start date
            if (startDate && startDate !== endDate) {
                g_form.setValue('end_date', startDate);
                g_form.showFieldMsg('end_date', 'End date set to match start date for half-day leave.', 'info');
            }
        } 
        // --- SCENARIO 2: "Is Half Day" is UNCHECKED ---
        else {
            // If we have both dates, calculate the range
            if (startDate && endDate) {
                var start = new Date(startDate);
                var end = new Date(endDate);

                // Check if end date is before start date
                if (start > end) {
                     g_form.showFieldMsg('end_date', 'End date cannot be before start date.', 'error');
                     g_form.setValue('duration', '1.00'); // Reset to 1
                     return; // Stop further calculation
                }

                var timeDiff = end.getTime() - start.getTime();
                var dayDiff = (timeDiff / (1000 * 3600 * 24)) + 1;
                g_form.setValue('duration', dayDiff.toFixed(2));
            } else {
                // If one or both dates are missing, default to 1 day
                g_form.setValue('duration', '1.00');
            }
        }
    } catch (e) {
        console.error("Error in updateLeaveDetails: " + e.message);
    }
}
