sys_script.script:
(function executeRule(current, previous) {
    try {
        gs.info('=== Enforce leave balance - Complete ===');

        // Basic validation
        if (!current.requested_by || !current.leave_type) {
            gs.addErrorMessage('Required fields are missing');
            current.setAbortAction(true);
            return;
        }

        // Get year as string
        var yearDate = current.start_date ? 
            new GlideDateTime(current.start_date) :
            new GlideDateTime();

        var year = yearDate.getYearLocalTime().toString();

        // Find matching balance record
        var balanceGR = new GlideRecord('x_1800027_leve_sys_leave_balance');
        balanceGR.addQuery('employee', current.requested_by);
        balanceGR.addQuery('leave_type', current.leave_type);
        balanceGR.addQuery('year', year);
        balanceGR.query();

        if (!balanceGR.next()) {
            // No balance record found
            var leaveTypeName = current.leave_type.getDisplayValue();
            var msg = 'No leave balance record found for ' + leaveTypeName + ' in ' + year;
            gs.info('VALIDATION FAILED: ' + msg);
            gs.addErrorMessage(msg);
            current.setAbortAction(true);
            return;
        }

        // Check available balance
        var availableBalance = parseFloat(balanceGR.available_balance) || 0;
        var requested = parseFloat(current.duration) || 0;

        if (requested > availableBalance) {
            var msg = 'Insufficient leave balance. Available: ' +
                availableBalance.toFixed(2) + ' days, Requested: ' + 
                requested.toFixed(2) + ' days';
            gs.info('VALIDATION FAILED: ' + msg);
            gs.addErrorMessage(msg);
            current.setAbortAction(true);
            return;
        }

        gs.info('Validation passed - sufficient balance available');

    } catch(ex) {
        gs.error('Error in validation: ' + ex);
        gs.addErrorMessage('Error occurred: ' + ex.message);
        current.setAbortAction(true);
    }
})(current, previous);
