sys_script_include.script:
var LeaveManagementUtils = Class.create();
LeaveManagementUtils.prototype = {
    initialize: function() {
    },

    calculateDuration: function() {
        var startDate = g_form.getValue('start_date');
        var endDate = g_form.getValue('end_date');
        var isHalfDay = g_form.getValue('is_half_day') == 'true';

        if (startDate && endDate) {
            var start = new Date(startDate);
            var end = new Date(endDate);

            var timeDiff = end.getTime() - start.getTime();
            var dayDiff = Math.ceil(timeDiff / (1000 * 3600 * 24)) + 1;

            if (isHalfDay) {
                dayDiff = 0.5;
            }

            g_form.setValue('duration', dayDiff);
        }
    },

    type: 'LeaveManagementUtils'
};

// Global function to be called from client scripts
function calculateDuration() {
    var utils = new LeaveManagementUtils();
    utils.calculateDuration();
}
