sys_script_include.script:
var LeaveApprovalUtils = Class.create();
LeaveApprovalUtils.prototype = Object.extendsObject(AbstractAjaxProcessor, {
    approveLeave: function() {
        try {
            var sysId = this.getParameter('sysparm_sys_id');
            var gr = new GlideRecord('x_1800027_leve_sys_leave_request');
            if (gr.get(sysId)) {
                gr.setWorkflow(false);
                gr.status = 'approved';
                if (gr.update()) {
                    return 'Leave request approved successfully';
                }
                return 'Error: ' + gr.getLastErrorMessage();
            }
            return 'Error: Record not found';
        } catch(ex) {
            return 'Error: ' + ex.message;
        }
    },
    type: 'LeaveApprovalUtils'
});
